import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { SkillBar } from './components/SkillBar';
import { ProjectCard } from './components/ProjectCard';

const projects = [
  {
    id: 1,
    title: 'Digital Twin AI Assistant',
    category: 'AI',
    link: 'https://github.com/AaronRodriguez1/Digital_Twin_Project',
    img: '/images/digital_twin.jpg',
    summary: 'Built a custom digital twin agent leveraging PyTorch transformers, deployed to Raspberry Pi with TensorFlow Lite, and integrated AWS SageMaker for continuous retraining.',
  },
  {
    id: 2,
    title: 'Marathon Simulator',
    category: 'Simulation',
    link: 'https://github.com/AaronRodriguez1/Marathon_Simulator',
    img: '/images/marathon.jpg',
    summary: 'Simulated full marathon routes using Strava GPX data and elevation profiles, applying regression-based models to predict per-segment pace and finish times.',
  },
  {
    id: 3,
    title: 'SuperTuxKart Autonomous Racing',
    category: 'Deep Learning',
    link: 'https://github.com/AaronRodriguez1/SuperTuxKart',
    img: '/images/Supertuxkart.jpg',
    summary: 'Developed MLP, Transformer, and CNN planners in PyTorch to predict waypoints from track boundaries and raw images, achieving <0.20m longitudinal error.',
  },
  {
    id: 4,
    title: 'Agent Librarian Chat Bot',
    category: 'AI',
    link: 'https://github.com/AaronRodriguez1/agent_librarian',
    img: '/images/agent_librarian.jpg',
    summary: 'Implemented a retrieval-augmented chatbot using LangChain & FAISS to index video transcripts against research papers, with evaluation and embedding fine-tuning.',
  },
  {
    id: 5,
    title: 'ARTEMIS Framework',
    category: 'HPC',
    link: 'https://drive.google.com/drive/u/0/folders/1qjkYaE_pZ9kz88GmCzTYy1PDB3riOZru',
    img: '/images/artemis.jpg',
    summary: 'Contributed C++/Python HPC algorithms for electrodynamic simulations, containerized workflows with Docker, and managed CI/CD on Azure.',
  },
  {
    id: 6,
    title: 'Helmholtz Coil Redesign',
    category: 'Hardware',
    link: 'https://drive.google.com/file/d/1QgDr9hSOS6OWZN6ofkFEVdZrwOWM0PNS/view?usp=sharing',
    img: '/images/helmholtz.jpg',
    summary: 'Engineered a rotating table system to measure magnetic moments, scripting Python data pipelines to reduce processing time by 25%.',
  },
  {
    id: 7,
    title: 'Microcontroller – Adafruit',
    category: 'Embedded',
    link: 'https://drive.google.com/file/d/1lGjJY4ZmLspQPsHW_qyV8XOkIp9ZakMq/view?usp=sharing',
    img: '/images/microcontroller.jpg',
    summary: 'Programmed Adafruit microcontrollers for analog circuit DAQ, designed Python scripts for real-time sensor control and data visualization.',
  },
  {
    id: 8,
    title: 'Conveyor Belt Control System',
    category: 'Controls',
    img: '/images/conveyor.jpg',
    summary: 'Designed PLC logic and SCADA simulations in Siemens TIA Portal to auto-detect and sort boxes, integrating optical sensors and OPC data retrieval.',
  },
  {
    id: 9,
    title: 'Spacecraft Reentry Simulation',
    category: 'Simulation',
    link: 'https://drive.google.com/file/d/1sVsSRON682CwLFZJZsSJ5sNKxvyaOq-x/view?usp=sharing',
    img: '/images/reentry.jpg',
    summary: 'Created an explicit Euler Python model for spacecraft thermal profiles, incorporating temperature-dependent conductivity and radiation boundaries.',
  },
  {
    id: 10,
    title: 'Summer Research Fellowship',
    category: 'Outreach',
    link: 'https://tasty-night-9160.glideapp.io/dl/6471c6',
    img: '/images/surf.jpg',
    summary: 'Built a science-demo app with Python & Glide for community outreach, enabling interactive exploration of 17 physics experiments.',
  },
  {
    id: 11,
    title: 'Spring Research Fellowship',
    category: 'Research',
    link: 'https://drive.google.com/file/d/1w3UVfO_1EDTZAkR72K0rlBgeT_bQ0z2f/view?usp=sharing',
    img: '/images/spring_research.jpg',
    summary: 'Investigated HVAC contaminant transport using pressure transducers, designing scale-model units in SolidWorks for experimental validation.',
  },
  {
    id: 12,
    title: 'Trash Collection Device',
    category: 'Design',
    link: 'https://drive.google.com/file/d/1MJ7_lzSh6CtxqITUzT3lU9trQge1i0Hb/view?usp=sharing',
    img: '/images/trash.jpg',
    summary: 'Led design & prototyping of an automated river trash collector, integrating data logging and PLC automation for sampling cycles.',
  },
  {
    id: 13,
    title: 'Drone Leg Connection',
    category: 'Analysis',
    link: 'https://drive.google.com/file/d/1vAAb5tcuS1KjMux3Up093MAa053dNZYW/view?usp=sharing',
    img: '/images/drone.jpg',
    summary: 'Performed FEA in SolidWorks for leg connection designs, conducting fatigue and crack-growth analyses, and validating with 3D-printed models.',
  },
];

const skills = [
  { name: 'JavaScript', level: 90 },
  { name: 'React', level: 85 },
  { name: 'Node.js', level: 80 },
  { name: 'Python', level: 88 },
  { name: 'Docker', level: 75 },
  { name: 'AWS', level: 70 },
];

export default function App() {
  const [filter, setFilter] = useState('All');
  const categories = ['All', 'AI', 'Simulation', 'Deep Learning', 'HPC', 'Hardware', 'Embedded', 'Controls', 'Outreach', 'Research', 'Design', 'Analysis'];
  const filtered = filter === 'All' ? projects : projects.filter(p => p.category === filter);

  return (
    <div className="min-h-screen bg-gray-50 text-gray-800">
      {/* Nav */}
      <nav className="bg-white shadow sticky top-0 z-50">
        <div className="container mx-auto px-6 lg:px-20 flex justify-between items-center py-4">
          <a href="#" className="text-2xl font-bold text-black">Aaron Rodriguez</a>
          <ul className="hidden md:flex space-x-6">
            {['About', 'Skills', 'Projects', 'Contact'].map(section => (
              <li key={section}><a href={`#${section.toLowerCase()}`} className="hover:text-indigo-500 transition">{section}</a></li>
            ))}
          </ul>
        </div>
      </nav>

      {/* Hero */}
      <header id="about" className="bg-gradient-to-r from-gray-600 to-blue-500 text-white py-20">
        <div className="container mx-auto text-center">
          <motion.img
            src="/images/aaron.jpg"
            alt="Aaron Rodriguez"
            className="mx-auto rounded-full w-48 h-48 border-4 border-white shadow-lg mb-6 object-cover"
            initial={{ scale: 0 }}
            animate={{ scale: 1 }}
            transition={{ duration: 0.5 }}
          />
          <motion.h1 className="text-5xl font-extrabold mb-4" initial={{ y: -50, opacity: 0 }} animate={{ y: 0, opacity: 1 }}>
            Aaron Rodriguez
          </motion.h1>
          <p className="max-w-2xl mx-auto text-lg">I build intelligent systems & full-stack web apps that solve real world problems.</p>
        </div>
      </header>

      {/* Skills */}
      <section id="skills" className="container mx-auto px-6 lg:px-20 py-16">
        <h2 className="text-3xl font-bold text-indigo-600 text-center mb-8">Skills</h2>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
          {skills.map(skill => <SkillBar key={skill.name} {...skill} />)}
        </div>
      </section>

      {/* Projects */}
      <section id="projects" className="bg-gray-100 py-16">
        <div className="container mx-auto px-6 lg:px-20">
          <h2 className="text-3xl font-bold text-indigo-600 text-center mb-8">Projects</h2>
          <div className="flex flex-wrap justify-center mb-6 space-x-2">
            {categories.map(cat => (
              <button
                key={cat}
                className={`px-3 py-1 rounded-full mb-2 ${
                  filter === cat
                    ? 'bg-indigo-600 text-white'
                    : 'border border-indigo-600 text-indigo-600'
                }`}
                onClick={() => setFilter(cat)}
              >
                {cat}
              </button>
            ))}
          </div>
          <motion.div layout className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
            {filtered.map(project => <ProjectCard key={project.id} project={project} />)}
          </motion.div>
        </div>
      </section>

      {/* Contact */}
      <footer id="contact" className="bg-indigo-600 text-white py-12">
        <div className="container mx-auto px-6 lg:px-20 text-center">
          <h4 className="text-xl font-semibold mb-2">Let's Connect</h4>
          <p>Email: <a href="mailto:aaronnathanrodriguez@gmail.com" className="underline">aaronnathanrodriguez@gmail.com</a></p>
          <p className="mt-2">LinkedIn: <a href="https://www.linkedin.com/in/ARSTMU" className="underline">ARSTMU</a></p>
        </div>
      </footer>
    </div>
  );
}
